import Screen from './components/Screen';
import ButtonBox from './components/ButtonBox';
import Button from './components/Button';
import CalcProvider from './context/CalcContext';

const btnValues = [
  'C',
  '+-',
  '%',
  '/',
  7,
  8,
  9,
  'x',
  4,
  5,
  6,
  '-',
  1,
  2,
  3,
  '+',
  0,
  '.',
  '=',
];

function App() {
  return (
    <CalcProvider>
      <div className="App">
        <Screen />
        <ButtonBox>
          {btnValues.map((btn, i) => (
            <Button value={btn} key={i} />
          ))}
        </ButtonBox>
      </div>
    </CalcProvider>
  );
}

export default App;
