import { useContext } from 'react';
import { CalcContext } from '../context/CalcContext';

const getClassName = (value) => {
  const className = {
    '+': 'bg-orange',
    '-': 'bg-orange',
    x: 'bg-orange',
    '/': 'bg-orange',
    '=': 'bg-orange',
    C: 'bg-gray',
    '+-': 'bg-gray',
    '%': 'bg-gray',
  };

  return className[value];
};

const getId = (value) => {
  const idName = {
    0: 'zero',
    1: 'one',
    2: 'two',
    3: 'three',
    4: 'four',
    5: 'five',
    6: 'six',
    7: 'seven',
    8: 'eight',
    9: 'nine',
    '+': 'add',
    '-': 'subtract',
    x: 'multiply',
    '/': 'divide',
    '.': 'decimal',
    '=': 'equals',
  };

  return idName[value];
};

function Button({ value }) {
  const [setCalc] = useContext(CalcContext);

  commaClick = () => {
    setCalc({
      ...calc,
      num: 29,
    });
  };

  const handleBtnClick = () => {
    const result = {
      '.': commaClick,
    };

    return result[value];
  };

  return (
    <button
      onClick={handleBtnClick}
      id={getId(value)}
      className={`button ${getClassName(value) ? getClassName(value) : ''}`}
    >
      {value}
    </button>
  );
}

export default Button;
