// const getStyleName(value) {
//     const className
// }

const getId = (value) => {
  const idName = {
    0: 'zero',
    1: 'one',
    2: 'two',
    3: 'three',
    4: 'four',
    5: 'five',
    6: 'six',
    7: 'seven',
    8: 'eight',
    9: 'nine',
    '+': 'add',
    '-': 'subtract',
    x: 'multiply',
    '/': 'divide',
    '.': 'decimal',
    '=': 'equals',
  };

  return idName[value];
};

function Button({ value }) {
  return <button id={getId(value)}>{value}</button>;
}

export default Button;
